state = obj.getVar(0);

switch (state) {
case 0:
    lbl.text = "Выключен";
    break;
case 1:
    lbl.text = "Ожидание разрешения запуска...";
    break;
case 2:
    lbl.text = "Готов к старту. Нажмите СТАРТ";
    break;
case 3:
    lbl.text = "Запуск...";
    break;
case 4:
    lbl.text = "Работает";
    break;
case 5:
    lbl.text = "Останов...";
    break;
case 6:
    lbl.text = "Приостановлен";
    break;
default:
    lbl.text = "?";
}

if (state >0) {
    obj.setBackgroundColor(0x00FF00);
} else {
    obj.setBackgroundColor(0xCCCCCC);
}

